class Builder extends StatelessWidget {

  LocalStorage box = new LocalStorage("box");

  Builder({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {

    box.setItem("configured", false);
    bool cfgd = box.getItem("configured");


    if(cfgd){
      return Generator();
    }else {

      return Page();
    }
  }
}


