class Generator extends StatefulWidget {
  const Generator({Key? key}) : super(key: key);

  @override
  _GeneratorState createState() => _GeneratorState();
}

Future<Activity> getActivity() async {
  var rng = new Random();
  LocalStorage box = new LocalStorage("box");
  List<String> types = box.getItem("types");
  final response = await http
      .get(Uri.parse("http://www.boredapi.com/api/activity?type=${types[rng.nextInt(types.length)]}"));

  if (response.statusCode == 200) {
    // If the server did return a 200 OK response,
    // then parse the JSON.
    return Activity.fromJson(jsonDecode(response.body));
  } else {
    // If the server did not return a 200 OK response,
    // then throw an exception.
    throw Exception('Failed to load album');
  }
}

Future _refresh(BuildContext context) async {
  return getActivity();
}

class _GeneratorState extends State<Generator> {

  late Future<Activity> activity;

  @override
  Widget build(BuildContext context) {

    activity = getActivity();

    return Scaffold(
      body: Stack(
        children: [
          Padding(
            padding: const EdgeInsets.all(40.0),
            child: Center(
              child: FutureBuilder<Activity>(
                future: activity,
                builder: (context, snapshot) {
                  if(snapshot.hasData) {
                    return Text(
                      snapshot.data!.activity,
                      style: TextStyle(
                        fontFamily: 'Georgia',
                        fontSize: 30.0,
                        color: const Color(0xFF0C2F3B),
                        fontWeight: FontWeight.w700,
                      ),
                      textAlign: TextAlign.center,
                    );
                  }else if(snapshot.hasError) {
                    return Text(
                      'Error',
                      style: TextStyle(
                        fontFamily: 'Georgia',
                        fontSize: 30.0,
                        color: const Color(0xFFEE312A),
                        fontWeight: FontWeight.w700,
                      ),
                      textAlign: TextAlign.center,
                    );
                  }
                  return const CircularProgressIndicator(
                    color: Color(0xFF101010),
                    strokeWidth: 5,
                  );
                },

              ),
            ),
          ),
          Positioned(
            left: 10,
